mapper.update:
update freeboard set fb_subject=#{fb_subject}, fb_content=#{fb_content}

    ,fb_photo=#{fb_photo}

where fb_idx=#{fb_idx}

mapper.select:
SELECT fb_like
FROM freeboard
WHERE fb_idx = #{fb_idx}

SELECT fb_dislike
FROM freeboard
WHERE fb_idx = #{fb_idx}

mapper.delete:
DELETE FROM fboard_like
WHERE fb_idx = #{fb_idx}
  AND m_idx = #{m_idx}
  AND likeStatus = 1

DELETE FROM fboard_like
WHERE fb_idx = #{fb_idx}
  AND m_idx = #{m_idx}
  AND likeStatus = 2

mapper.select:
SELECT likeStatus
FROM fboard_like
WHERE fb_idx = #{fb_idx} AND m_idx = #{m_idx};
