mapper.select:
select count(*)
from member
where m_email = #{m_email}

select count(*)
from member
where m_nickname = #{m_nickname}

select count(*)
from member
where m_email = #{m_email}
  and m_pass = #{m_pass}
  and m_type = 0

select count(*)
from companymember
where cm_email = #{cm_email}
  and cm_pass = #{cm_pass}

select *
from member
where m_email = #{m_email}

select *
from companymember
where cm_email = #{cm_email}

select ai_idx
from academyinfo
where ai_name = #{ai_name}

select salt
from member
where m_email = #{m_email}

select salt
from companymember
where cm_email = #{cm_email}

select count(*)
from companymember
where cm_email = #{cm_email}

select count(*)
from companymember
where cm_compname = #{cm_compname}

select count(*)
from member
where m_name = #{m_name}
  and m_tele = #{m_tele}

select m_email, m_type, m_date
from member
where m_name = #{m_name}
  and m_tele = #{m_tele}

select count(*)
from companymember
where cm_name = #{cm_name}
  and cm_cp = #{cm_cp}

select cm_email, cm_compname, cm_date
from companymember
where cm_name = #{cm_name}
  and cm_cp = #{cm_cp}

select count(*)
from member
where m_email = #{m_email}
  and m_name = #{m_name}
  and m_tele = #{m_tele}
  and m_type = 0

select count(*)
from member
where m_email = #{m_email}
  and m_name = #{m_name}
  and m_type = 0

mapper.update:
update member
set m_pass=#{m_pass}, salt=#{salt}
where m_email=#{m_email}
and m_name=#{m_name}
and m_type=0;

mapper.select:
select count(*)
from companymember
where cm_email = #{cm_email}
  and cm_name = #{cm_name}
  and cm_cp = #{cm_cp}

mapper.update:
update companymember
set cm_pass=#{cm_pass}, salt=#{salt}
where cm_email=#{cm_email}
  and cm_name=#{cm_name}

mapper.select:
select count(*)
from companymember
where cm_email = #{cm_email}
  and cm_name = #{cm_name}

select count(*)
from companymember
where cm_reg=#{cm_reg}

mapper.update:
update member
set m_photo=#{m_photo}
where m_idx=99
