mapper.select:
SELECT qb_like
FROM qboard
WHERE qb_idx = #{qb_idx}

SELECT qb_dislike
FROM qboard
WHERE qb_idx = #{qb_idx}

mapper.delete:
DELETE FROM qboard_like
WHERE qb_idx = #{qb_idx}
  AND m_idx = #{m_idx}
  AND likeStatus = 1

DELETE FROM qboard_like
WHERE qb_idx = #{qb_idx}
  AND m_idx = #{m_idx}
  AND likeStatus = 2

mapper.select:
SELECT likeStatus
FROM qboard_like
WHERE qb_idx = #{qb_idx} AND m_idx = #{m_idx};
